def _from_settings(cls, settings: Settings, crawler: (Crawler | None)=None
    ) ->Self:
    mwlist = cls._get_mwlist_from_settings(settings)
    middlewares = []
    enabled = []
    for clspath in mwlist:
        try:
            mwcls = load_object(clspath)
            if crawler is not None:
                mw = build_from_crawler(mwcls, crawler)
            else:
                mw = MiddlewareManager._build_from_settings(mwcls, settings)
            middlewares.append(mw)
            enabled.append(clspath)
        except NotConfigured as e:
            if e.args:
                logger.warning('Disabled %(clspath)s: %(eargs)s', {
                    'clspath': clspath, 'eargs': e.args[0]}, extra={
                    'crawler': crawler})
    logger.info('Enabled %(componentname)ss:\n%(enabledlist)s', {
        'componentname': cls.component_name, 'enabledlist': pprint.pformat(
        enabled)}, extra={'crawler': crawler})
    return cls(*middlewares)

