def get_crawler(spidercls: (type[Spider] | None)=None, settings_dict: (dict
    [str, Any] | None)=None, prevent_warnings: bool=True) ->Crawler:
    """Return an unconfigured Crawler object. If settings_dict is given, it
    will be used to populate the crawler settings with a project level
    priority.
    """
    from scrapy.crawler import CrawlerRunner
    settings: dict[str, Any] = {**get_reactor_settings(), **settings_dict or {}
        }
    runner = CrawlerRunner(settings)
    crawler = runner.create_crawler(spidercls or DefaultSpider)
    crawler._apply_settings()
    return crawler

