def to_bytes(text: (str | bytes), encoding: (str | None)=None, errors: str=
    'strict') ->bytes:
    """Return the binary representation of ``text``. If ``text``
    is already a bytes object, return it as-is."""
    if isinstance(text, bytes):
        return text
